        PRIMARY KEY (t_custom_service_id)
);
CREATE UNIQUE INDEX t_custom_service_unik ON net.t_custom_service(probe_name, lower(service_name), e_protocol_id);

--
-- rules
--
CREATE SEQUENCE net.t_rule_serial;
